def longest_palindrome(string):
    longest_length = 0
    longest_palindrome = ""
    for left in range(len(string)):
        for right in range(left + 1, len(string) + 1):
            sub_string = string[left:right]
            if sub_string == sub_string[::-1] and len(sub_string) > longest_length:
                longest_length = len(sub_string)
                longest_palindrome = sub_string
    return longest_palindrome

